package com.example;

import java.util.Map;

import org.corfudb.runtime.CorfuRuntime;
import org.corfudb.runtime.collections.CorfuTable;

import io.netty.util.internal.ThreadLocalRandom;


/**
 * Hello world!
 *
 */
public class App {
    private final static int num = 1000000;
    private final static String fourKB = "";
    public static void main(String[] args) {
        CorfuRuntime rt = new CorfuRuntime("128.110.155.128:9000");

        rt.connect();

        Map<Integer, String> map = rt.getObjectsView().build().setStreamName("A").setType(CorfuTable.class).open();

        for(int i = 0; i < num; i++) {
            map.put(i, fourKB);
            int randomNum = ThreadLocalRandom.current().nextInt(0, i);
            map.get(randomNum);
            System.out.println("put " + i);
        }

    }
}
